import * as React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import TabBarIcon from '../components/TabBarIcon';
import HomeScreen from '../screens/HomeScreen';
import MainScreen from '../screens/MainScreen';
import Screen2 from '../screens/Screen2';
import Screen3 from '../screens/Screen3';
import Clrs from "../constants/Colors";
import { Text, View } from 'react-native';
import { AntDesign,Feather,FontAwesome5 } from "@expo/vector-icons";

const BottomTab = createBottomTabNavigator();
const INITIAL_ROUTE_NAME = 'Home';

export default function BottomTabNavigator({ navigation, route }) {
  navigation.setOptions({ headerTitle: getHeaderTitle(route) });

  return (
    <BottomTab.Navigator initialRouteName={INITIAL_ROUTE_NAME}
      tabBarOptions={{
        activeBackgroundColor: Clrs.DGreen,
        inactiveBackgroundColor: Clrs.DGreen,
        style: {
          borderTopWidth: 0,
        }
      }}
    >
      <BottomTab.Screen
        name="Main"
        component={MainScreen}
        options={{
          tabBarLabel: ({ focused }) => { return <Text style={{ color: focused ? Clrs.BYellow : Clrs.tabIconDefault }}>محول العملات</Text> },
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="md-home" />,
        }}
      />
      <BottomTab.Screen
        name="Sc2"
        component={Screen2}
        options={{
          tabBarLabel: ({ focused }) => { return <Text style={{ color: focused ? Clrs.BYellow : Clrs.tabIconDefault }}>قائمة العملات</Text> },
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="md-list" />,
        }}
      />
      <BottomTab.Screen
        name="Sc3"
        component={Screen3}
        options={{
          tabBarLabel: ({ focused }) => { return <Text style={{ color: focused ? Clrs.BYellow : Clrs.tabIconDefault }}>عن التطبيق</Text> },
          tabBarIcon: ({ focused }) => <TabBarIcon focused={focused} name="info" />,
        }}
      />
    </BottomTab.Navigator>
  );
}

function getHeaderTitle(route) {
  const routeName = route.state?.routes[route.state.index]?.name ?? INITIAL_ROUTE_NAME;

  switch (routeName) {
    case 'Home':
      return 'Home Page';
    case 'Sc2':
      return 'Page 2';
    case 'Sc3':
      return 'Page 3';
  }
}
