import * as React from 'react';
import { Text, View } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { LinearGradient } from 'expo-linear-gradient';
import Clrs from "../constants/Colors";

export default function CustomHeader({ title, isHome, navigation }) {
  return (
    <LinearGradient colors={[Clrs.BGreen, Clrs.DGreen]} locations={[0, 1]} style={{
      flexDirection: 'row', height: 64,
      elevation: 1,

      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.20,
      shadowRadius: 1.41,

      elevation: 2,
    }}>
      {
        isHome ?
          <View style={{ flex: 1, justifyContent: 'center' }}>
            <TouchableOpacity
              onPress={() => navigation.toggleDrawer()}
            >
              <Ionicons
                name="md-menu"
                size={30}
                style={{ marginLeft: 5 }}
                color={Clrs.BYellow}
              />
            </TouchableOpacity>
          </View>
          :
          <View style={{
            flex: 1, justifyContent: 'center',
            shadowColor: "#000",
            shadowOffset: {
              width: 0,
              height: 5,
            },
            shadowOpacity: 0.34,
            shadowRadius: 6.27,

            elevation: 10,
          }}>
            <TouchableOpacity
              onPress={() => navigation.goBack()}
            >
              <Text style={{ width: 30, height: 30, marginLeft: 5 }}>{"(="}</Text>
            </TouchableOpacity>
          </View>
      }
      <View style={{ flex: 1.5, justifyContent: 'center' }}>
        <Text style={{ textAlign: 'center', fontWeight: 500, color: Clrs.BYellow, fontSize: 18 }}>{title}</Text>
      </View>
      <View style={{ flex: 1 }}></View>
    </LinearGradient>
  )
}
